//ns1:ResultCode[1]

con:transfers.con:sourcePath:
declare namespace i='http://www.w3.org/2001/XMLSchema-instance';
declare namespace ns1='http://alstom.com/pacis/pis/ground/instantmessage/';
declare namespace s='http://schemas.xmlsoap.org/soap/envelope/';
//ns1:RequestId[1]

con:config.script:
def expectedNumberOfResponses = 2  //Number of notifications expected

//List of notification expected
def ListeNotifications = ["InstantMessageDistributionProcessing",
					 "InstantMessageDistributionSent",
					 "InstantMessageDistributionReceived"]

def mockService = testRunner.testCase.testSuite.project.getMockServiceByName("NotificationAppGroundMockService")
while( mockService.mockRunner.getMockResultCount() < expectedNumberOfResponses && testRunner.running )
{
Thread.sleep( 1000 )
}

//log.info(mockService.mockRunner.getMockResultCount())
def assertionList = []
for( i in 1..mockService.mockRunner.getMockResultCount() )
{
//log.info(i)
//def result = mockService.mockRunner.getMockResultAt( i-1 );
def result = mockService.mockRunner.mockResults.get(i-1 );
  if (result != null) 
  {
	def request = result.getMockRequest().getRequestContent()

	def gutils = new com.eviware.soapui.support.GroovyUtils( context );
	def holder = gutils.getXmlHolder(request);
	holder.namespaces["ns1"] = "http://alstom.com/pacis/pis/schema/appground/"

	//Get request values
	def notification = holder.getNodeValue("//ns1:SendNotification[1]/ns1:NotificationId[1]");
	def NotificationRequestID = holder.getNodeValue("//ns1:SendNotification[1]/ns1:RequestId[1]");

	log.info "Received " + notification + " Notification "

	def RequestID = context.expand('${#TestCase#RequestID}')

	//Validate Notification
	if (ListeNotifications[i-1] == notification)
	{
		log.info ListeNotifications[i-1] + " Assertion Pass"
	}
	else
	{
		log.info ListeNotifications[i-1] + " Assertion Failed"
		assertionList.add("Assertion Failed")
		assertionList.add(ListeNotifications[i-1])
	}
	// Check RequestID
	assert RequestID == NotificationRequestID : "Wrong RequestID"
  }
}
//validate result and throw exception to fail the test step (if response is invalid)
assert assertionList.isEmpty() : assertionList.toString()

con:testCase.con:tearDownScript:
def mockRunner = testRunner.testCase.testSuite.project.getMockServiceByName("NotificationAppGroundMockService").getMockRunner()
if (mockRunner != null && mockRunner.isRunning())
{
	mockRunner.stop()
}

